mapper.select:
select user.id, user.name, sex, birth, develope, password, role, introduction,role.name as rolename from user LEFT OUTER JOIN role ON user.role=role.id WHERE 1=1
and user.id like CONCAT('%',#{id,jdbcType=VARCHAR},'%')
and role = #{role,jdbcType=VARCHAR}
and sex = #{sex,jdbcType=VARCHAR}
and user.name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')
order BY role,id,user.name
